<dt id="tab8"><span> <?php _e('Inventory', 'com_shop'); ?></span></dt><dd>    <fieldset class="panelform">        <ul class="adminformlist">            <li>                <label for="stockrooms">                    <?php _e('Stock Room', 'com_shop'); ?>                </label>                <select onchange='jsShopAdminHelper.products.addStock(this);' name="stockrooms" id="stockrooms"  >                    <option value='' <?php echo empty($this->row->product->stockroom_id) ? "selected='selected'" : ''; ?> > <?php _e('no stockroom', 'com_shop'); ?></option>                    <?php while ($sr = $this->stockrooms->nextObject()) { ?>                        <option value='<?php echo $sr->id; ?>' <?php echo ($this->row->product->stockroom_id == $sr->id) ? "selected='selected'" : ''; ?> > <?php echo strings::stripAndEncode($sr->name); ?></option>                    <?php } ?>                </select>            </li>            <?php foreach ((array) $this->row->stockrooms as $o) { ?>                <li>                    <label for="stockroom<?php echo $o->id; ?>">                        <?php echo strings::stripAndEncode($o->name); ?>                     </label>                    <input type='text' id='stockroom<?php echo $o->id; ?>' name='stock_assoc[<?php echo (int) $o->id; ?>]' value='<?php echo (int) $o->stock; ?>' />                    <button class="btn btn-danger btn-small" onclick='jQuery(this.parentNode).remove();                            return false;'>                        <span class="icon-trash"></span>                       </button>                </li>            <?php } ?>            <li id="stockRoomsDelimiter"></li>            <li>                <label for="inventory_manage_stock">                    <?php _e('Manage Stock', 'com_shop'); ?>                </label>                <select class="select" name="manage_stock" id="inventory_manage_stock"  >                    <option value="global" <?php echo $this->row->product->manage_stock == 'global' ? 'selected="selected"' : ''; ?>  ><?php _e('global', 'com_shop'); ?></option>                    <option value="yes" <?php echo $this->row->product->manage_stock == 'yes' ? 'selected="selected"' : ''; ?>><?php _e('Yes', 'com_shop'); ?></option>                    <option value="no" <?php echo $this->row->product->manage_stock == 'no' ? 'selected="selected"' : ''; ?>><?php _e('No', 'com_shop'); ?></option>                </select>            </li>            <li>                <label for="inventory_stock_availability">                    <?php _e('Stock Availability', 'com_shop'); ?>                </label>                <select name="stock" id="inventory_stock_availability">                    <option value="global"  <?php echo $this->row->product->stock == 'global' ? 'selected="selected"' : ''; ?>><?php _e('global', 'com_shop'); ?></option>                    <option value="yes" <?php echo $this->row->product->stock == 'yes' ? 'selected="selected"' : ''; ?>><?php _e('In Stock', 'com_shop'); ?></option>                    <option value="no" <?php echo $this->row->product->stock == 'no' ? 'selected="selected"' : ''; ?>><?php _e('Out of Stock', 'com_shop'); ?></option>                </select>            </li>        </ul>    </fieldset></dd>